"""
Extrai os titulos dos videos dos cursos
"""

from bs4 import BeautifulSoup


def main(html):
    soup = BeautifulSoup(html, "html.parser")
    titulos = []

    for li in soup.select("li.sc-fJmtor"):
        spans = li.select("span.sc-fXSgeo.eBfUYv")
        if spans:
            titulo = spans[0].get_text(strip=True)
            if titulo:
                titulos.append(titulo)

    for t in titulos:
        print(t)

